// import reddit from "reddit.js"
const reddit = {}
reddit.top = function (subreddit) {
  return listing({
    subreddit: subreddit,
    resource: "top"
  }, ["t"]);
};

function listing(on, extras) {
  extras = extras || [];
  return withFilters(on, ["after", "before", "count", "limit", "show"].concat(extras));
}

function withFilters(on, filters) {
  var ret = {};
  on.params = on.params || {};
  filters = filters || [];

  var without = function (object, key) {
    var ret = {};
    for (var prop in object) {
      if (object.hasOwnProperty(prop) && prop !== key) {
        ret[prop] = object[prop];
      }
    }
    return ret;
  };

  var filter = function (f) {
    if (f === "show") {
      return function () {
        on.params[f] = "all";
        return without(this, f);
      };
    } else {
      return function (arg) {
        on.params[f] = arg;
        return without(this, f);
      };
    }
  };

  for (var i = 0; i < filters.length; i++) {
    ret[filters[i]] = filter(filters[i]);
  }
  ret.fetch = function (res, err) {
    getJSON(redditUrl(on), res, err);
  };
  return ret;
}

function redditUrl(on) {
  var url = "https://www.reddit.com/";
  var keys = function (object) {
    var ret = [];
    for (var prop in object) {
      if (object.hasOwnProperty(prop)) {
        ret.push(prop);
      }
    }
    return ret;
  };

  if (on.subreddit !== undefined) {
    url += "r/" + on.subreddit + "/";
  }
  url += on.resource + ".json";
  if (keys(on.params).length > 0) {
    var qs = [];
    for (var param in on.params) {
      if (on.params.hasOwnProperty(param)) {
        qs.push(encodeURIComponent(param) + "=" +
          encodeURIComponent(on.params[param]));
      }
    }
    url += "?" + qs.join("&");
  }
  return url;
}

function getJSON(url, res, err) {
  get(url, function (data) {
    res(JSON.parse(data));
  }, err);
}

function get(url, res, err) {
  var xhr = new XMLHttpRequest();
  xhr.open("GET", url, true);
  xhr.onload = function () {
    return res(xhr.response);
  };
  xhr.onerror = function () {
    if (err !== undefined) {
      return err(xhr.response);
    }
  };
  xhr.send();
}

const RedditFeed = () => {
  let posts = []
  console.log(JSON.stringify(reddit))
  reddit.top('CustomKeyboards').t('month').limit(50).fetch(function (res) {
    for (let i = 0; i < res.data.children.length; i++) {
      let data = res.data.children[i].data;

      if (!data.url.includes('i.redd.it')) continue;
      posts.push(
        <a href={"https://reddit.com" + data.permalink}>
          <div className="feed-item card">
            <img className="card-img-top" src={data.url} alt={data.title}/>
            <div className="card-body">
              <h5 className="card-title">{data.title}</h5>
              <p className="card-text text-muted">{"by " + data.author}</p>
            </div>
          </div>
        </a>
      );
    }
  });
  return (
    <>
      <div className="container">
        <div className="row">
          <div className="page-header header">
            <div id="feedHeader">
              <h1>Reddit Keyboard Feed<br/><small>A list of the top custom keyboards on r/CustomKeyboards from the past
                month</small></h1>
              <p>Check them out for some inspiration!</p>
            </div>

            <div className="feed-grid" id="redditFeed">
              {posts}
            </div>
          </div>
        </div>
      </div>
    </>
  )
};

export default RedditFeed;
